definition.script:
pipeline {
   agent any

   stages {
      stage('Clean Workspace') {
         steps {
            cleanWs()
            deleteDir()
         }
      }

      stage('Clone git rep') {
          steps {
              git 'https://github.com/endermAH/Netcrecker-project.git'
          }
      }

      stage('PostgreSQL installation') {
          steps {
              ansiColor('exterm') {
                  ansiblePlaybook(
                      playbook: '/root/.jenkins/workspace/netcracker/psql-vps-playbook.yml',
                      inventory: '/root/.jenkins/workspace/netcracker/inventory.ini',
                      colorized: true
                  )
              }
          }
      }

      stage('Liferay installation') {
          steps {
              ansiColor('exterm') {
                  ansiblePlaybook(
                      playbook: '/root/.jenkins/workspace/netcracker/liferay-vps-playbook.yml',
                      inventory: '/root/.jenkins/workspace/netcracker/inventory.ini',
                      colorized: true
                  )
              }
          }
      }
   }
}
